import Genre from './Genre';
import React, { Component } from 'react';

/*Hacer que el componente /components/GenresInDb.js sea ahora un componente stateful para poder 
conectarse con nuestro back-end a través de una API, la cual ya está armada y su endpoint es: 
/api/genres. Esta debe exponer de forma dinámica los géneros de las películas: */

let genres = [
    { genre: 'Acción' },
    { genre: 'Animación' },
    { genre: 'Aventura' },
    { genre: 'Ciencia Ficción' },
    { genre: 'Comedia' },
    { genre: 'Documental' },
    { genre: 'Drama' },
    { genre: 'Fantasia' },
    { genre: 'Infantiles' },
    { genre: 'Musical' }
]

class GenresInDb extends Component {
    constructor() {
        super(props);
        this.state = {
            genresList: []
        }
    }

    /*FETCH: Ahora debemos establecer la conexión con nuestro back-end con React, 
    podemos usar cualquier biblioteca AJAX, recomendamos utilizar
    windows.fetch. Esto lo debemos programar en el momento en que el componente se monte, 
    valiéndonos del método componentDidMount(): */
    componentDidMount() {
        fetch('/api/genres')
            .then(respuesta => {
                return respuesta.json()
            })
            .then(genres => {
                //console.log(genres)
                this.setState({ genresList: genres.data })
            })
            .catch(error => console.log(error))
    }

    /*RENDER: Ahora debemos proceder a renderizar lo que se le mostrará al usuario de forma dinámica: */
    render() {
        return (
            <React.Fragment>
                {/*<!-- Aquí va todo el contenido estático que tenemos en Genres in DB -->*/}
                {/*<!-- Aquí es donde vamos a enviar los datos obtenidos al componente Genre para que renderice de forma dinámica -->*/}
                <div className="card-body fondoCaja"> <div className="row">
                    {this.state.genresList.map((genre, index) => { return <Genre {...genre} key={index} /> })}
                </div>
                </div>
            </React.Fragment>)
    }


} /*EXPORTO COMPONENTE: */
export default GenresInDb;
